import React,{Component} from 'react';
import './Search.css';

const lurl = "https://zomatoajulypi.herokuapp.com/location";
const url = "https://zomatoajulypi.herokuapp.com/restaurant?stateId=1";


class Search extends Component{

    constructor(){
        super()

        this.state={
            location:''
        }
        console.log("inside constructor>>>")
    }
   
    renderCity = (data) => {
        console.log("<<<<",data,"<<<<<<")
             if(data){
                return data.map((item) =>{
                return(
                    <option value={item.state_id} key={item._id}>
                        {item.state}
                    </option>
                )
            })
        }
        
    }

    handelCity=(event)=>{
        let stateID = event.target.value
        fetch(`${restUrl}${stateID}`,{method:'GET'})
    }

    render(){
        console.log("inside render>>>")
        return(
            <div id="search">
                <div id="logo">
                    <span>Zo</span>
                </div>
                <div id="heading">
                    Find Best Place Near You
                </div>
            
                <div className="dropdown">
                    <select onChange={this.handelCity}>
                        <option>---SELECT YOUR CITY---</option>
                        {this.renderCity(this.state.location)}
                    </select>
                    <select id="restdrop">  
                        <option>---SELECT YOUR RESTURANT---</option>
                        <option>Wow Momos </option>
                        <option>Grill Bill</option>
                    </select>
                </div>
            </div> 
        )
    }
        componentDidMount(){
            console.log("inside componentDidMount>>>")
            fetch(lurl,{method: 'GET'})
            .then((res) => res.json())
            .then((data)=>{
                this.setState({location:data})
            })
            .catch((err)=>{
                console.log(err)
            })
        }



}
    

export default Search;
