import React,{Component} from 'react';
import './placeOrders.css';

const url = "http://zomatoajulypi.herokuapp.com/menuItem";

class PlaceOrder extends Component {

    constructor(props){
        super(props)

        this.state={
            id:Math.floor(Math.random()*100000),
            hotel_name:this.props.match.params.restName,
            name:'pruthiraj panda',
            email:'pruthirajpanda55@gmail.com',
            cost:0,
            phone:7978759755,
            address:'ky 34 sector 12',
            menuItem:''
        }
    }
    placeOrder = () => {
        let obj = this.state;
        obj.meuItem = sessionStorage.getItem('menu');
        console.log
    }
    renderItem=(data) => {
        if(data){
            return data.map((item) => {
                return(
                    <div className="orderItem" key={item.menu_id}>
                        <img src={item.menu_image} alt={item.menu_name}/>
                        <h3>{item.menu_name}</h3>
                        <h4>Rs. {item.menu_price}</h4>
                    </div>
                )
            })
        }
    }

    handelChange=(event)=>{
        this.setState({[event.target.name]:event.target.value})
    }

    render() {
        return(
            <div className="container">
                <div className="panel panel-primary">
                    <div className="panel-heading">
                        <h3>Your Order From Restaurant{this.state.hotel_name} </h3>
                    </div>
                    <div className="panel-body">
                        <div className="row">
                            <input type="hidden" name="cost" value={this.state.cost}/>
                            <input type="hidden" name="id" value={this.state.id}/>
                            <input type="hidden" name="hotel_name" value={this.state.hotel_name}/>
                            <div className="from-group col-md-6">
                                <label>Name</label>
                                <input className="form-control" name='name' value={this.state.name} onChange={this.handelChange}/>
                            </div>
                            <div className="from-group col-md-6">
                                <label>Email</label>
                                <input className="form-control" name='email' value={this.state.email} onChange={this.handelChange}/>
                            </div>
                            <div className="from-group col-md-6">
                                <label>phone</label>
                                <input className="form-control" name='phone' value={this.state.phone} onChange={this.handelChange}/>
                            </div>
                            <div className="from-group col-md-6">
                                <label>Address</label>
                                <input className="form-control" name='address' value={this.state.address} onChange={this.handelChange}/>
                            </div>
                        </div>
                        {this.renderItem(this.state.menuItem)}
                        <div className="row">
                            <div className="col-md-12">
                                <h2>Total Price is Rs.{this.state.cost}</h2>
                            </div>
                        </div>
                        <button className="btn btn-success" onClick={this.handleCheckout}>
                            Checkout
                        </button>
                    </div>
                </div>
            </div>
        )
    }
    componentDidMount(){
        let menuItem = sessionStorage.getItem('menu');
        let orderId = [];
        console.log(orderId)
        menuItem.split(',').map((item)=> {   
            orderId.push(parseInt(item))
            return 'ok'
        })
        fetch(url,{
            method: 'POST',
            headers:{
                'accept': 'application/json',
                'content-type':'application/json'
            },
            body:JSON.stringify(orderId)
        })
        .then((res)=> res.json())
        .then((data)=> {
            console.log(data)
            let totalPrice = 0;
            data.map((item)=> {
                totalPrice = totalPrice+Number(item.menu_price)
                return 'ok'
            })
            this.setState({menuItem:data,cost:totalPrice})
        })
    }
}
export default PlaceOrder;
