import React,{Component} from 'react';
import axios from 'axios';

const url = "https://zomatoajulypi.herokuapp.com/filter/1?hcost=1000&lcost=500"

class CostFilter extends Component {

    costFilter = (event) => {
        let mealId = this.props.mealId;
        let cost = (event.target.value).split('-')
        let lcost = cost[0];
        let hcost = cost[1];
        let cuisineUrl = "";
        if(cuisineId === ""){
            cuisineUrl=`${url}/${mealId}`
        }else{
            cuisineUrl=`${url}/${mealId}?cuisine=${cuisineId}`
        }
        axios.get(cuisineUrl)
        .then ((res) => {this.props.restPerCost(res.data)})
    }

    render(){
        return(
            <>
                <center><h3>Cost Filter</h3></center>
                <div style={{marginLeft:'15%'}} onChange={this.costFilter}>
                    <label className="radio">
                        <input type="radio" name="cuisine" value=""/>All
                    </label>
                    <label className="radio">
                        <input type="radio" name="cuisine" value="100-300"/>100-300 
                    </label>
                    <label className="radio">
                        <input type="radio" name="cuisine" value="301-700"/>301-700
                    </label>
                    <label className="radio">
                        <input type="radio" name="cuisine" value="701-1000"/>701-1000
                    </label>
                    <label className="radio">
                        <input type="radio" name="cuisine" value="1001-2000"/>1001-2000
                    </label>
                </div>
            </>
        )
    }
}

export default CostFilter;
